function testFunc() {
    for (var i = 0; i < 5; i++) {                 // 0 ~ 5 ，向主线程发送5次数据之后该线程函数执行完成
        __threadPostMessage(0, i)                 // 发送数据到主线程
        var msg = __threadPeekMessage()           // 监听发送到当前线程的数据
        Log("from main msg:", msg)
        Sleep(500)
    }
    Log("testFunc执行完毕")
}

function main() {
    var testThreadId = __Thread(testFunc)             // 创建线程，Id为1 

    var results = null 
    for (var i = 0; i < 10; i++) {
        // 检测线程Id为testThreadId的并发线程执行函数是否执行完成
        results = results ? results : __threadJoin(testThreadId, -1)
        if (results) {
            Log("results:", results)
        } else {
            __threadPostMessage(1, i)                 // 发送数据到Id为1的线程，即本例中执行testFunc函数的线程
        }

        var msg = __threadPeekMessage(-1)             // 监听发送到当前线程（主线程）的数据
        Log("from testFunc msg:", msg)
        Sleep(500)
    }
}

